import stlyed from 'styled-components'
import { type } from 'os'

type ButtonfuProps = {
    userClicked : Boolean
} 
export const Buttonfu = stlyed.div<ButtonfuProps>`
    button{
        background : ${({ userClicked }) => 
            userClicked 
                ? '#000'
                : '#fff' 
        }
    }
`
