import React, { useState } from "react";
import "./styles.css";

// components
import QuestionCard from "./components/QuestionCard";

// api and type
import { fetchQuestion, Difficulty, QuestionsState } from './Api'

export default function App() {

  type AnswersObject = {
    question: string,
    answer: string,
    correct: boolean,
    correctAnswer: string
  }

  const totalQuestion = 10
  // setting states 
  const [loading, setloading] = useState(false)
  const [question, setquestion] = useState<QuestionsState[]>([])
  const [number, setnumber] = useState(0)
  const [userAnswer, setAnswer] = useState<AnswersObject[]>([])
  const [score, setscore] = useState(0)
  const [gameOver, setgameOver] = useState(true)


  // funtions
  const startQuiz = async () => {
    setloading(true)
    setgameOver(false)

    const newQuestion = await fetchQuestion(totalQuestion, Difficulty.EASY)
    setquestion(newQuestion)
    setscore(0)
    setAnswer([])
    setnumber(0)
    setloading(false)
  };
  const checkAnswer = (e: React.MouseEvent<HTMLButtonElement>) => { };
  const nextQuestion = () => { };
  return (
    <div>
      <h1>Quiz App</h1>
      {gameOver || userAnswer.length === totalQuestion ? (
        <button onClick={startQuiz}>Start Quiz</button>
      ) : null}
      <div>
        {!gameOver ? <p>Socre</p> : null}
        {loading && <p>loading ......</p>}
      </div>
      <div>
        {!loading && !gameOver && (
          <QuestionCard
            question={question[number].question}
            questionNo={number + 1}
            callback={checkAnswer}
            userAnswer={userAnswer ? userAnswer[number] : undefined}
            totalQuestion={totalQuestion}
            answers={question[number].answers}
          />
        )}
      </div>
      {!gameOver && !loading && userAnswer.length === number +1 && number !== totalQuestion -1 ? (
      <div>
        <button onClick={nextQuestion}>Next Question</button>
      </div>
      ) : null}
    </div>
  );
}
